class TestamentCreater():
    def __init__(self,myAddress, ganache_url = "http://127.0.0.1:7545" ):
        self.ganache_url = ganache_url
        self.myAddress = myAddress
        self.contractCompiled = None
        self.web3 = None
        connected = self.connect()
        print("Connected to the server status:{}".format(connected))

    def connect(self):
        web3 = Web3(Web3.HTTPProvider(self.ganache_url))
        web3.eth.defaultAccount = self.myAddress
        self.web3 = web3
        return(web3.isConnected())

    def getContractCompiled(self):
        abi = json.loads(abiString)
        bytecode = byteCodeDict['object']
        TestamentContractCompiled = self.web3.eth.contract(abi = abi, bytecode = bytecode)
        self.contractCompiled = TestamentContractCompiled
        return(TestamentContractCompiled)

    def signTestamentContent(self, content, pk):
        testamentContent = content
        private_key = pk
        signableMessage = encode_defunct(text=testamentContent)

        #sign message
        signed_message = w3.eth.account.sign_message(signableMessage, private_key=private_key)
        messageHash = signed_message['messageHash']
        sig = signed_message['signature']
        messageHashHex = messageHash.hex()
        sigHex = sig.hex()
        return(messageHashHex, sigHex)

    def createTestamentContract(self, messageHashHex, sigHex):
        txHash = self.contractCompiled.constructor(messageHashHex, sigHex).transact()
        txRec = self.web3.eth.waitForTransactionReceipt(txHash)
        contractHash = txRec.contractAddress
        return(contractHash)

    def getMessageHashHex(self, contractAddress):
        contractInstance = self.web3.eth.contract(address=contractAddress, abi=json.loads(abiString))
        try:
            return(contractInstance.functions.getMessageHashHex().call())
        except:
            return(None)

    def getSigHex(self, contractAddress):
        contractInstance = self.web3.eth.contract(address=contractAddress, abi=json.loads(abiString))
        try:
            return(contractInstance.functions.getSigHex().call())
        except:
            return(None)
